mapper.select:
select * from DB_SUB_DOC_INFO a inner join DB_DOCUMENT_INFO b on a.INFO_ID=b.ID
left join (select SUB_ID,RECEIVER_NAME as deal_user_name from (select SUB_ID,RECEIVER_NAME,row_number() over(partition by SUB_ID order by CREATED_TIME desc) as num from DB_SUB_DOC_TRACKING) where num=1) f on a.id=f.sub_id left join (select INFO_ID,USER_NAME as leader_name ,LEADER_COMMENT as leader_content,CREATED_TIME as leader_time,row_number() over(partition by info_id order by created_time asc) as num from DB_DOCUMENT_SZPS) c on a.info_id = c.info_id where (num = 1 or num is null)
and a.SUB_DEPT_ID = #{orgId}
and b.DOC_TITLE like '%'||#{search}||'%'
and a.DOC_STATUS = #{docStatus}
and b.CUIBAN_FLAG ='1'
and a.DOC_STATUS < 12 order by a.DOC_STATUS,a.CREATED_TIME desc
limit #{offset}, #{limit}
