mapper.select:
select * from DB_SUB_DOC_INFO a inner join DB_DOCUMENT_INFO b on a.INFO_ID=b.ID
left join (select SUB_ID,RECEIVER_NAME as deal_user_name from (select SUB_ID,RECEIVER_NAME,row_number() over(partition by SUB_ID order by CREATED_TIME desc) as num from DB_SUB_DOC_TRACKING) where num=1) f on a.id=f.sub_id left join (
select s.INFO_ID,s.USER_NAME as leader_name,s.LEADER_COMMENT as leader_content,s.CREATED_TIME as leader_time, row_number() over(partition by s.info_id order by d.sort asc) as num from DB_DOCUMENT_SZPS s inner join DB_ROLE_SET d on s.USER_NAME=d.USER_NAME order by d.sort asc ) c on a.info_id = c.info_id left join (select distinct info_id,listagg(sub_dept_name || nvl2(DECODE(undertaker_name,'',null,'NULL',null,undertaker_name),'/' || undertaker_name || nvl2(DECODE(undertaker_phone,'',null,'NULL',null,undertaker_phone),'('||undertaker_phone||')',null),null), ',') within group(order by created_time desc) over(partition by info_id) under_depts from DB_SUB_DOC_INFO) e on a.INFO_ID = e.info_id where (num = 1 or num is null)
and a.SUB_DEPT_ID = #{orgId}
and b.DOC_TITLE like '%'||#{search}||'%'
and a.DOC_STATUS = #{docStatus}
and b.CUIBAN_FLAG ='1'
and b.STATUS=1 order by a.DOC_STATUS,a.CREATED_TIME desc
limit #{offset}, #{limit}
