mapper.select:
select * from DB_SUB_DOC_INFO a 
inner join DB_DOCUMENT_INFO b on a.INFO_ID=b.ID where 1=1 

   and a.SUB_DEPT_ID = #{orgId}


	and b.DOC_TITLE like '%'||#{search}||'%'


	and a.DOC_STATUS = #{docStatus}

 	and a.DOC_STATUS < 12 order by a.DOC_STATUS,a.CREATED_TIME desc

	limit #{offset}, #{limit}

WITH V_RESULT as(select SUB_ID from 
(select SUB_ID, row_number() over(partition by SUB_ID order by CREATED_TIME desc) as num
from DB_SUB_DOC_TRACKING where 1=1 

	and RECEIVER_ID = #{loginUserId}
)
where num =1)	

select * from DB_SUB_DOC_INFO a inner join v_result b on a.ID = b.SUB_ID	
left join DB_DOCUMENT_INFO c on a.INFO_ID=c.ID  where 1=1	

	and c.DOC_TITLE like '%'||#{search}||'%'


	and a.DOC_STATUS = #{docStatus}

 	and a.DOC_STATUS < 12 order by a.DOC_STATUS, a.CREATED_TIME desc

	limit #{offset}, #{limit}
